// Ignore updates for three days or take you to the update page


require_once('workflows.php');
$w = new Workflows();
$q = "{query}";
$now = time();

if ($q == 'dont-update') {
  $w->set( 'update_ignore_date', $now , 'settings.plist' );
  shell_exec(`osascript -e 'tell application "Alfred 2" to search "darksky"'`);
} else {
  shell_exec("open {$q}");
}
